    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
   try{  
       Lecturer l = new Lecturer(txtname.getText(), txtpno.getText(), Integer.parseInt(txtage.getText()), txtaddress.getText(),txtgender.getText(),Float.parseFloat(txtsalary.getText()),txtlid.getText(),txtposition.getText(),txtqualification.getText(),txtemail.getText(),txtsubject.getText());
            Lecturerlist.add(l);
            
             JOptionPane.showMessageDialog(rootPane, "Lecturer Added Sucessfully" , "Info", 1);
   }catch(NumberFormatException e)
   {
       JOptionPane.showMessageDialog(rootPane, "Empty Value or error input", "Info", 0);
   }
    }//GEN-LAST:event_jButton1ActionPerformed

