    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            Lecturer l = new Lecturer(txtname.getText(), txtpno.getText(), Integer.parseInt(txtage.getText()), txtgender.getText(), txtlid.getText(), txtposition.getText(), txtqualification.getText(), txtemail.getText(), txtsubject.getText());
            boolean result = ls.AddLecturer(l);
            if (result == true) {
                JOptionPane.showMessageDialog(rootPane, "Addded Hall Details To Database", "Sucess", 2);
            } else {
                JOptionPane.showMessageDialog(rootPane, "Error Adding Hall Details To Database", "Error", 0);
            }

        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(rootPane, "Empty Value or error input", "Info", 0);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

